mapper.select:
select * from iims_ai_chat_dialogue


        topic_id = #{topicId}


        and is_deleted = #{isDeleted}


        and create_by = #{createBy}


order by create_time desc

SELECT *
FROM (
         SELECT *
         FROM iims_ai_chat_dialogue
         WHERE topic_id = #{topicId}
           AND is_deleted = 0
         ORDER BY create_time DESC
         LIMIT #{size}
     ) AS query
ORDER BY create_time;

mapper.update:
update iims_ai_chat_dialogue
content = #{content}, is_deleted = #{isDeleted}, is_star = #{isStar}, feedback_status = #{feedbackStatus}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

update iims_ai_chat_dialogue
set is_deleted = #{isDeleted}, update_by = #{updateBy}, update_time = #{updateTime}
where topic_id in

    #{id}
