mapper.select:
select * from iims_ai_chat_topic


        id = #{id}


        and title like concat('%', #{title}, '%')


        and is_deleted = #{isDeleted}


        and create_by = #{createBy}


order by create_time desc

mapper.update:
update iims_ai_chat_topic
set is_deleted = #{isDeleted}, update_by = #{updateBy}, update_time = #{updateTime}
where id in

    #{id}

update iims_ai_chat_topic
title = #{title}, top = #{top}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
