mapper.update:
update iims_integral_admin
name = #{name}, username = #{username}, password = #{password}, phone = #{phone}, sex = #{sex}, id_Number = #{idNumber}, root = #{root}, email = #{email}, avatar = #{avatar}, organization = #{organization}, introduction = #{introduction}, role = #{role}, is_disable = #{isDisable}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

update iims_integral_admin
set is_deleted = #{isDeleted}, update_by = #{updateBy}, update_time = #{updateTime}
where id in

    #{id}

mapper.select:
select * from iims_integral_admin


        and email = #{email}


        and username = #{username}


        and phone = #{phone}


        and id_number = #{idNumber}

SELECT m.perms FROM iims_integral_admin a JOIN iims_integral_role r ON JSON_CONTAINS(a.role, CONCAT('\"', CAST(r.id AS CHAR), '\"'))
                            JOIN iims_integral_menu m ON JSON_CONTAINS(r.menus, CONCAT('\"', CAST(m.id AS CHAR), '\"'))
WHERE a.id = #{userId} AND a.is_disable = 0;
