mapper.update:
update iims_integral_article
title = #{title}, cover = #{cover}, summary = #{summary}, dict_tag_ids = #{dictTagIds}, dict_category_id = #{dictCategoryId}, weight = #{weight}, read_num = #{readNum}, type = #{type}, is_deleted = #{isDeleted}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select id, title, cover, dict_category_id, dict_tag_ids, weight, create_time, update_time, create_by as userInfoId
from iims_integral_article

    and is_deleted = 0

        and title like concat('%', #{title}, '%')


        and type = #{type}


        and create_time >= #{startDate}


        and create_time <= #{endDate}
