mapper.update:
update iims_integral_menu
title = #{title}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, name = #{name}, component = #{component}, icon = #{icon}, menu_type = #{menuType}, perms = #{perms}, visible = #{visible}, redirect = #{redirect}, status = #{status}, is_frame = #{isFrame}, update_time = #{updateTime}, update_by = #{createBy}
where id = #{id}

mapper.select:
select * from iims_integral_menu

    and title like concat('%', #{title}, '%')
    and status = #{status}

order by order_num asc , create_time desc;
